    private void bttRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttRegistrarActionPerformed

        String nome = txtNome.getText();
        int idade = Integer.parseInt(txtIdade.getText());
        char sexo = (txtSexo.getText().charAt(0));
        Pessoa natacao = new Pessoa(nome, idade, sexo);
        if(natacao.getSexo() == 'f' || natacao.getSexo() == 'F' || natacao.getSexo() == 'm' || natacao.getSexo() == 'M'){
            lista.insert(natacao.toString());
        }
        else{
            txtaArea.setText("Sexo Inválido, aluno não adicionado \n");
        }

        int i = 60;
        if(natacao.getIdade() >= i){
            filaI.enqueue(natacao);
            txtaArea.setText("Aluno adicionado com sucesso na lista de idosos \n");
        }
        if(natacao.getSexo() == 'f' && natacao.getIdade() < i){
            filaF.enqueue(natacao);
            txtaArea.setText("Aluno adicionado com sucesso na lista de mulheres \n");
        }
        if(natacao.getSexo() == 'm' && natacao.getIdade() < i){
            filaM.enqueue(natacao);
            txtaArea.setText("Aluno adicionado com sucesso na lista de homens \n");
        }
        if(natacao.getSexo() == 'F' && natacao.getIdade() < i){
            filaF.enqueue(natacao);
            txtaArea.setText("Aluno adicionado com sucesso na lista de mulheres \n");
        }
        if(natacao.getSexo() == 'M' && natacao.getIdade() < i){
            filaM.enqueue(natacao);
            txtaArea.setText("Aluno adicionado com sucesso na lista de homens \n");
        }
        txtNome.setText("");
        txtIdade.setText("");
        txtSexo.setText("");
    }//GEN-LAST:event_bttRegistrarActionPerformed

