    private void bttAtenderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttAtenderActionPerformed
        if(filaI.isEmpty() && filaF.isEmpty() && filaM.isEmpty()){
            txtaArea.setText("Sem alunos cadastrados");
        }
        else{
            if(!filaI.isEmpty()){
                Pessoa i = (Pessoa)filaI.dequeue();
                txtaArea.setText("Em Atendimento: \n" + i.toString());
                controle = (filaF.isEmpty()? 0 : 1);
            }
            else if(!filaF.isEmpty() && controle == 0){
                Pessoa f= (Pessoa)filaF.dequeue();
                txtaArea.setText("Em Atendimento: \n" + f.toString());
                controle = (filaM.isEmpty()? 0 : 1);
            }
            else if(!filaM.isEmpty() && controle == 1){
                Pessoa m = (Pessoa)filaM.dequeue();
                txtaArea.setText("Em Atendimento: \n" + m.toString());
                controle = 0;
            }
        }
    }//GEN-LAST:event_bttAtenderActionPerformed

